widgets.onValue:
// script property
var fallTime = 0.2,
    fps = 24,
    maxValue = 1,
    step = maxValue / fps / fallTime

setTimeout(()=>{
  if (value <= 0) clearTimeout()
  set("this", value - step)
}, 1000 / fps)

